// src/components/student/FoodOrderForm.jsx
import React, { useState, useEffect } from 'react';
import { Card, Form, Button, DatePicker, Select, Table, InputNumber, Input, message, Spin, Alert, List, Typography, Divider, Row, Col, Image, Space, Modal } from 'antd';
import { PlusOutlined, MinusOutlined, ShoppingCartOutlined, ClockCircleOutlined, SaveOutlined, DeleteOutlined } from '@ant-design/icons';
import { messAPI, studentAPI } from '../../services/api';
import moment from 'moment';

const { Option } = Select;
const { TextArea } = Input;
const { Title, Text } = Typography;

const FoodOrderForm = () => {
  const [form] = Form.useForm();
  const [foodItems, setFoodItems] = useState([]);
  const [categories, setCategories] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState('all');
  const [cartItems, setCartItems] = useState([]);
  const [loading, setLoading] = useState(false);
  const [submitting, setSubmitting] = useState(false);
  const [error, setError] = useState(null);
  const [showCart, setShowCart] = useState(false);

  useEffect(() => {
    fetchFoodItems();
    fetchCategories();
  }, []);

  const fetchFoodItems = async (category = 'all') => {
    setLoading(true);
    try {
      const params = { is_available: true };
      if (category !== 'all') {
        params.category = category;
      }
      
      const response = await messAPI.getSpecialFoodItems(params);
      if (response.data.success) {
        setFoodItems(response.data.data);
      } else {
        message.error('Failed to load food items: ' + (response.data.message || 'Unknown error'));
      }
    } catch (error) {
      console.error('Failed to fetch food items:', error);
      setError('Failed to load food items. Please try again later.');
    } finally {
      setLoading(false);
    }
  };

  const fetchCategories = async () => {
    try {
      const response = await messAPI.getSpecialFoodItems();
      if (response.data.success) {
        // Extract unique categories
        const uniqueCategories = [...new Set(response.data.data.map(item => item.category))];
        setCategories(uniqueCategories);
      }
    } catch (error) {
      console.error('Failed to fetch categories:', error);
    }
  };

  const handleCategoryChange = (value) => {
    setSelectedCategory(value);
    fetchFoodItems(value);
  };

  const addToCart = (item) => {
    setCartItems(prev => {
      const existingItem = prev.find(i => i.id === item.id);
      if (existingItem) {
        return prev.map(i => 
          i.id === item.id ? { ...i, quantity: i.quantity + 1 } : i
        );
      } else {
        return [...prev, { ...item, quantity: 1, special_instructions: '' }];
      }
    });
    message.success(`${item.name} added to cart`);
  };

  const updateCartItemQuantity = (id, quantity) => {
    if (quantity <= 0) {
      setCartItems(prev => prev.filter(item => item.id !== id));
    } else {
      setCartItems(prev => 
        prev.map(item => 
          item.id === id ? { ...item, quantity } : item
        )
      );
    }
  };

  const updateCartItemInstructions = (id, instructions) => {
    setCartItems(prev => 
      prev.map(item => 
        item.id === id ? { ...item, special_instructions: instructions } : item
      )
    );
  };

  const removeFromCart = (id) => {
    setCartItems(prev => prev.filter(item => item.id !== id));
  };

  const clearCart = () => {
    setCartItems([]);
  };

  const calculateTotal = () => {
    return cartItems.reduce((sum, item) => sum + (item.price * item.quantity), 0);
  };

  const handleSubmit = async (values) => {
    if (cartItems.length === 0) {
      message.warning('Please add at least one item to your cart');
      return;
    }

    setSubmitting(true);
    try {
      const orderData = {
        items: cartItems.map(item => ({
          food_item_id: item.id,
          quantity: item.quantity,
          special_instructions: item.special_instructions
        })),
        requested_time: values.requested_time.toISOString(),
        notes: values.notes
      };

      const response = await studentAPI.createFoodOrder(orderData);
      
      if (response.data.success) {
        message.success('Food order placed successfully');
        setCartItems([]);
        form.resetFields();
        setShowCart(false);
      } else {
        setError('Failed to place order: ' + (response.data.message || 'Unknown error'));
      }
    } catch (error) {
      console.error('Failed to place order:', error);
      setError('Failed to place order. Please try again later.');
    } finally {
      setSubmitting(false);
    }
  };

  const renderFoodItemCard = (item) => {
    return (
      <Card
        hoverable
        style={{ marginBottom: 16 }}
        cover={
          item.image_url ? 
          <Image
            alt={item.name}
            src={item.image_url}
                        fallback="data:image/png;base64,"
            style={{ height: 200, objectFit: 'cover' }}
          /> : 
          <div style={{ height: 200, display: 'flex', justifyContent: 'center', alignItems: 'center', background: '#f0f0f0' }}>
            No Image
          </div>
        }
        actions={[
          <Button 
            type="primary" 
            icon={<PlusOutlined />} 
            onClick={() => addToCart(item)}
          >
            Add to Order
          </Button>
        ]}
      >
        <Card.Meta
          title={<span>{item.name} - â‚¹{parseFloat(item.price).toFixed(2)}</span>}
          description={
            <>
              <p>{item.description || 'No description available'}</p>
              {item.preparation_time_minutes && (
                <p><ClockCircleOutlined /> Prep Time: {item.preparation_time_minutes} minutes</p>
              )}
            </>
          }
        />
      </Card>
    );
  };

  const renderCartModal = () => {
    return (
      <Modal
        title={<div><ShoppingCartOutlined /> Your Order</div>}
        visible={showCart}
        onCancel={() => setShowCart(false)}
        footer={null}
        width={700}
      >
        {cartItems.length === 0 ? (
          <div style={{ textAlign: 'center', padding: '20px 0' }}>
            <Text>Your cart is empty</Text>
          </div>
        ) : (
          <Form
            form={form}
            layout="vertical"
            onFinish={handleSubmit}
            initialValues={{ requested_time: moment().add(1, 'hour') }}
          >
            <List
              itemLayout="horizontal"
              dataSource={cartItems}
              renderItem={item => (
                <List.Item
                  actions={[
                    <Button 
                      type="text" 
                      danger 
                      icon={<DeleteOutlined />}
                      onClick={() => removeFromCart(item.id)}
                    />
                  ]}
                >
                  <List.Item.Meta
                    title={item.name}
                    description={
                      <div>
                        <p>â‚¹{parseFloat(item.price).toFixed(2)} x {item.quantity} = â‚¹{(item.price * item.quantity).toFixed(2)}</p>
                        <Input.TextArea 
                          placeholder="Special instructions"
                          value={item.special_instructions}
                          onChange={(e) => updateCartItemInstructions(item.id, e.target.value)}
                          rows={2}
                          style={{ marginTop: 8 }}
                        />
                      </div>
                    }
                  />
                  <div>
                    <Space>
                      <Button 
                        icon={<MinusOutlined />} 
                        onClick={() => updateCartItemQuantity(item.id, item.quantity - 1)}
                      />
                      <InputNumber 
                        min={1} 
                        value={item.quantity} 
                        onChange={(value) => updateCartItemQuantity(item.id, value)}
                        style={{ width: 60 }}
                      />
                      <Button 
                        icon={<PlusOutlined />} 
                        onClick={() => updateCartItemQuantity(item.id, item.quantity + 1)}
                      />
                    </Space>
                  </div>
                </List.Item>
              )}
              footer={
                <div style={{ textAlign: 'right' }}>
                  <Title level={4}>Total: â‚¹{calculateTotal().toFixed(2)}</Title>
                </div>
              }
            />
            
            <Divider />
            
            <Form.Item
              name="requested_time"
              label="When would you like your order ready?"
              rules={[{ required: true, message: 'Please select a time' }]}
            >
              <DatePicker 
                showTime 
                format="YYYY-MM-DD HH:mm" 
                disabledDate={(current) => current && current < moment().startOf('day')}
                disabledTime={() => ({
                  disabledHours: () => [...Array(moment().hour()).keys()],
                  disabledMinutes: (hour) => {
                    if (hour === moment().hour()) {
                      return [...Array(moment().minute() + 30).keys()];
                    }
                    return [];
                  }
                })}
                style={{ width: '100%' }}
              />
            </Form.Item>
            
            <Form.Item
              name="notes"
              label="Additional Notes"
            >
              <TextArea rows={3} placeholder="Any additional notes for your order" />
            </Form.Item>
            
            <div style={{ textAlign: 'right' }}>
              <Space>
                <Button onClick={() => clearCart()}>Clear Cart</Button>
                <Button onClick={() => setShowCart(false)}>Continue Shopping</Button>
                <Button
                  type="primary"
                  htmlType="submit"
                  loading={submitting}
                  icon={<SaveOutlined />}
                >
                  Place Order
                </Button>
              </Space>
            </div>
          </Form>
        )}
      </Modal>
    );
  };

  return (
    <Card title="Special Food Order" bordered={false}>
      {error && (
        <Alert
          message="Error"
          description={error}
          type="error"
          showIcon
          closable
          style={{ marginBottom: 16 }}
          onClose={() => setError(null)}
        />
      )}
      
      <div style={{ marginBottom: 16, display: 'flex', justifyContent: 'space-between' }}>
        <Select
          style={{ width: 200 }}
          placeholder="Filter by category"
          value={selectedCategory}
          onChange={handleCategoryChange}
        >
          <Option value="all">All Categories</Option>
          {categories.map(category => (
            <Option key={category} value={category}>{category}</Option>
          ))}
        </Select>
        
        <Button 
          type="primary" 
          icon={<ShoppingCartOutlined />} 
          onClick={() => setShowCart(true)}
          badge={{ count: cartItems.length }}
        >
          View Cart ({cartItems.length})
        </Button>
      </div>
      
      <Spin spinning={loading}>
        <Row gutter={[16, 16]}>
          {foodItems.map(item => (
            <Col xs={24} sm={12} md={8} lg={8} xl={6} key={item.id}>
              {renderFoodItemCard(item)}
            </Col>
          ))}
        </Row>
        
        {foodItems.length === 0 && !loading && (
          <div style={{ textAlign: 'center', padding: '40px 0' }}>
            <Text>No food items available in this category</Text>
          </div>
        )}
      </Spin>
      
      {renderCartModal()}
    </Card>
  );
};

export default FoodOrderForm;
